<template>
  <v-row class="my-5" :class="smallSize ? 'small-font' : ''">
    <v-img
      src="data:image/jpg;base64,"
      max-width="200"
      max-height="180"
      aspect-ratio="0.85"
      contain
      :class="smallSize ? 'mt-5' : ''"
    ></v-img>
    <v-col v-if="!smallSize" class="d-flex justify-center align-center">
      <!-- <v-btn class="mx-2 button-borders" fab small @click="decrement">
        <v-icon color="blue-grey">mdi-minus</v-icon>
      </v-btn>
      <span class="title">{{ bpm }}</span>
      <v-btn class="mx-2 button-borders" fab small @click="increment">
        <v-icon color="blue-grey">mdi-plus</v-icon>
      </v-btn> -->
      <v-select
        v-model="quantity"
        :items="[1, 2, 3, 4, 5, 6]"
        label="КОЛ-ВО"
        outlined
        style="max-width: 30%"
        @input="changeQuantity"
      ></v-select>
    </v-col>
    <v-col>
      <p class="font-weight-bold mb-1 title">
        {{ productItem.product.productName }}
      </p>
      <p v-if="!smallSize" class="mb-2">
        <span class="caption">КОЛ-ВО: </span>
        <strong>{{ productItem.quantity }}</strong>
      </p>
      <v-select
        v-if="smallSize"
        v-model="quantity"
        :items="[1, 2, 3, 4, 5, 6]"
        dense
        style="max-width: 70%"
        @input="changeQuantity"
      ></v-select>
      <p class="mb-2" style="text-decoration: line-through;">
        <span class="caption">ЦЕНА: </span>
        <strong>{{ productItem.product.productPrice }}</strong> руб.
      </p>
      <p class="mb-2 red--text text--darken-4">
        <span class="caption">СКИДКА: </span>
        <strong>{{ productItem.product.discount }}%</strong>
      </p>
      <p class="mb-2 red--text text--darken-4">
        <span class="caption">ЦЕНА СО СКИДКОЙ: </span>
        <strong>950</strong> руб.
      </p>
      <p class="mb-2">
        <span class="caption">СУММА: </span><strong>950</strong> руб.
      </p>
    </v-col>
    <v-col cols="1" class="d-flex justify-center align-center">
      <v-btn icon link to="#" class="mr-3" @click="deleteFromCart">
        <v-icon color="red accent-4" class="display-2">mdi-delete</v-icon>
      </v-btn>
    </v-col>
  </v-row>
</template>

<script>
export default {
  props: {
    productItem: {
      type: Object,
      required: true
    }
  },
  data() {
    return {
      quantity: 1
    }
  },
  computed: {
    smallSize() {
      return this.$vuetify.breakpoint.smAndDown
    }
  },
  created() {
    this.quantity = this.productItem.quantity
  },
  methods: {
    changeQuantity() {
      this.$store.commit('cart/CHANGE_QUANTITY', {
        product: this.productItem,
        quantity: this.quantity
      })
    },
    deleteFromCart() {
      this.$store.commit('cart/REMOVE_FROM_CART', {
        product: this.productItem
      })
      this.$toasted
        .error(`${this.productItem.product.productName} удален из корзины`)
        .goAway(2000)
    }
  }
}
</script>

<style scoped>
.small-font {
  font-size: 0.6rem;
}
</style>
